import ncurses

let stdscr = initscr() # <-- CORRECT: Always capture stdscr
discard cbreak()
discard noecho()

# 1. Check for color support
if not has_colors():
  discard endwin()
  echo "Your terminal does not support color."
  quit(1)

# 2. Start color mode
discard start_color()

# 3. Define color pairs
