discard init_pair(1, COLOR_RED, COLOR_BLACK)
# Pair 2: Green text on White background
discard init_pair(2, COLOR_GREEN, COLOR_WHITE)

# Example 1: Bold text
discard attron(cint(A_BOLD)) # <-- FIX: Cast to cint
discard printw("This is BOLD text.\n")
